CREATE TABLE `zt_milestone` (`id` int(11) NOT NULL AUTO_INCREMENT,`product` int(11) NOT NULL COMMENT '项目id',`name` varchar(50) NOT NULL COMMENT '名称',`date` datetime DEFAULT NULL COMMENT '日期',`isContract` enum('0','1') NOT NULL COMMENT '合同里程碑',`pri` enum('1','2','3','4') NOT NULL COMMENT '重要程度',`completed` enum('0','1') NOT NULL COMMENT '是否完成',`comment` text DEFAULT NULL COMMENT '备注',`createdDate` datetime NOT NULL DEFAULT current_timestamp() COMMENT '创建时间',`createdBy` varchar(50) NOT NULL COMMENT '创建用户',PRIMARY KEY (`id`)) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4